
public class maxof2num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Print max of a,b,c ==> ");
		int a = 10;
		int b = 50;
		int c = 30;
		if(a>=b&&a>=c) {
			System.out.println(a);
		}
		else if(b>=a&&b>=c) {
			System.out.println(b);
		}
		else
			System.out.println(c);
		

	}

}
