
public class print10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		System.out.println("Output is :");
		for(i=1;i<=10;i++) {
			System.out.println(i);
		}
		
		
		System.out.println("Output is :");
		for (i = 10;i>0;i--) {
			System.out.println(i);
		}
		
	

	}

}
